import React, { Component } from 'react'
import { returnStatement } from '@babel/types';

export default class Greeting extends Component {
    render() {
        return (
            <div className="card time-card">
                <div className="card-content">
                    <span style={{fontSize: '2.5rem'}}><sup style={{fontSize: '1.4rem'}}>It's </sup>{this.getClock()}</span>
                    <h5 style={{marginTop: '0'}}>{this.getTime()}</h5>
                    <p>Here are your pending tasks.</p>
                </div>
            </div>
        )
    }

    getTime() {
        const date = new Date();
        let time = {
            clock: time.toLocaleTimeString().replace(/([\d]+:[\d]{2})(:[\d]{2})(.*)/, "$1$3"),
            greeting: '',
            icon: ''
        }
        
        let hour = date.getHours();
        if(hour > 5 && hour < 12) {
            time.greeting = "Good Morning!"
            time.icon = '<ion-icon name="partly-sunny"></ion-icon>';
            }
        else if(hour == 12){
            time.greeting = "It's Noon!"
            time.icon = '<ion-icon name="sunny"></ion-icon>';
        }
        else if(hour > 12 && hour < 17){
            time.greeting = "It's sunny afternoon!"
            time.icon = '<ion-icon name="sunny"></ion-icon>';
        }
        else if(hour > 17 && hour < 20){
            time.greeting = "What a lovely evening we have today!"
            time.icon = '<ion-icon name="partly-sunny"></ion-icon>';
        }
        else if(hour > 20  && hour < 25){
            time.greeting = "Working late are we?"
            time.icon = '<ion-icon name="moon"></ion-icon>';
        }
        else if(hour > 0  && hour < 5){
            time.greeting = "Why aren't you sleeping?"
            time.icon = '<ion-icon name="moon"></ion-icon>';
        }
        else
            return "What time is it again?";

            
        // switch(hour){
