import React from 'react';
import PropTypes from 'prop-types';

class TodoItem extends React.Component {
    getStyle(){
        return{
            color: this.props.todo.completed ? '#848484' : '#5ba6ab',
            textDecoration: this.props.todo.completed ? 'line-through' : 'none'
        }
    }

    markComplete = () => {
        console.log(this.props.todo.completed);
    }

    render(){
        return (
            <div>
                <a className="collection-item">
                    <label>
                        <input type="checkbox" style={checkBoxStyle} className="filled-in" onChange={this.markComplete}/>
                        <span style={this.getStyle()}>{this.props.todo.title}</span>
                    </label>
                </a>
            </div>
        );
    }
}

const checkBoxStyle = {
    border: '2px solid #949494',
    backgroundColor: '#949494'
}

TodoItem.propTypes = {
    todo: PropTypes.object.isRequired
}

export default TodoItem;
