import React, {Component} from 'react';
import './App.css';
import Todos from './components/Todos';

class App extends Component {
  state = {
    todos: [
      {
        id: 1,
        title: 'Take out trash.',
        completed: false
      },
      {
        id: 2,
        title: 'Do homework.',
        completed: true
      },
      {
        id: 3,
        title: 'Learn React.',
        completed: false
      },
      {
        id: 4,
        title: 'Take Notes.',
        completed: false
      },
    ]
  }

  markComplete = (id) => {
    this.setState({ todos: this.state.todos.map((todo) => {
      if(todo.id === id)
        todo.completed = !todo.completed
        return todo;
    })});
  }

  delTodo = (id) => {
    //console.log("Del " + id);
    this.setState({todo: [...this.state.todos.filter(todo =>
      todo.id === id)]});
  }

  render(){
    return (
      <div className="row">
        <div className="col s12 m6">
            <Todos todos={ this.state.todos } delTodo={this.delTodo} markComplete={this.markComplete}/>
        </div>
      </div>
    );
  };
}

export default App;
