import React from 'react';
import PropTypes from 'prop-types';

class TodoItem extends React.Component {
    getStyle(){
        return{
            color: this.props.todo.completed ? '#848484' : '#5ba6ab',
            textDecoration: this.props.todo.complete ? 'line-through' : 'line-through'
        }
    }

    render(){
        return (
            <a className="collection-item" style={this.getStyle()} href="javascript:void(0)">{this.props.todo.title}</a>
        );
    }
}

TodoItem.propTypes = {
    todo: PropTypes.object.isRequired
}

export default TodoItem;
