import React, { Component } from 'react'
import { returnStatement } from '@babel/types';

export default class Greeting extends Component {
    render() {
        return (
            <div className="card">
                <div className="card-content">
                    <span style={{fontSize: '2.5rem'}}><sup style={{paddingTop: '1rem'}}>It's </sup>{this.getClock()}</span>
                    <h5 style={{marginTop: '0'}}>{this.getGreeting()}</h5>
                    <p>Here are your pending tasks.</p>
                </div>
            </div>
        )
    }

    getClock(){
        const time = new Date();
        return time.toLocaleTimeString().replace(/([\d]+:[\d]{2})(:[\d]{2})(.*)/, "$1$3");
    }

    getGreeting() {
        const date = new Date();
        let hour = date.getHours();

        if(hour > 5 && hour < 12)
            return "Good Morning!";
        else if(hour == 12)
            return "It's Noon!";
        else if(hour > 12 && hour < 17)
            return "It's sunny afternoon!";
        else if(hour > 17 && hour < 20)
            return "What a lovely evening we have today!";
        else if(hour > 20  && hour < 25)
            return "Working late are we?";
        else if(hour > 0  && hour < 5)
            return "Why aren't you sleeping?";
        else
            return "What time is it again?";

            
